import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { Context } from "./Storage";
import CircularProgress from "@mui/material/CircularProgress";
import Box from "@mui/material/Box";
import { NotFound } from "./NotFound";

function Welcome() {
  const [mainData, setMainData] = useState({});
  const [state, setState] = useContext(Context);
  const [loaderShow, setLoaderShow] = useState(true);

  useEffect(() => {
    const sendRequest = async () => {
      await axios
        .get("http://localhost:5000/api/user", { headers: { Authorization: `Bearer ${state.token}` } })
        .then((res) => {
          setState((prev) => ({ ...prev, isLoggedIn: true }));
          setMainData(res.data);
          setLoaderShow(false);
        })
        .catch((err) => {
          console.log(err);
          setLoaderShow(false);
        });
    };

    sendRequest();
  }, []);

  return (
    <>
      {!loaderShow ? (
        state.isLoggedIn ? (
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <div className="welcome-ctn">
              <h1 style={{ textAlign: "center" }}>Welcome {mainData.user} ðŸ‘‹</h1>
              {/* <h1>{loggedIn ? `Welcome ${data?.user} ðŸ‘‹` : `Please Login First!`}</h1> */}
            </div>
            <Button
              style={{
                padding: "10px",
                backgroundColor: "#0a0a0a",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
                marginTop: "2rem",
              }}
              onClick={() => {
                console.log(mainData);
              }}
            >
              Console Log User
            </Button>
          </div>
        ) : (
          <NotFound />
        )
      ) : (
        <Box
          sx={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <CircularProgress />
        </Box>
      )}
    </>
  );
}

export default Welcome;
