import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { MuiOtpInput } from "mui-one-time-password-input";
import { Context } from "./Storage";
import { NotFound } from "./NotFound";

function OTP() {
  const history = useNavigate();
  const [state, setState] = useContext(Context);
  const [otp, setOtp] = useState(1);
  const [otpFetched, setOtpFetched] = useState(0);
  const { token } = useState(state.token);
  const [data, setData] = useState({});

  const handleChange = (newValue) => {
    setOtp(newValue);
  };

  const sendRequest = async () => {
    const res = await fetch("http://localhost:5000/api/verification", {
      method: "GET",
    });

    const data = await res.json();
    console.log(data);
    setData(data);
    setOtpFetched(data.code);
  };

  useEffect(() => {
    if (otp === String(otpFetched)) {
      setState((prev) => ({ ...prev, isLoggedIn: true }));
      history("/user");
    }
  }, [state, otp, history, otpFetched, setState]);

  React.useEffect(() => {
    sendRequest();

    setTimeout(() => {
      setOtpFetched(null);
    }, 5000);
  }, []);

  return (
    <div>
      {token === "" ? (
        <NotFound />
      ) : (
        <div className="otp-ctn">
          <div className="otp-ctn-ctn">
            <h1 className="otp-ctn-header">OTP Verification</h1>
            <h3>
              {
                // Split data.message to get the email
                data.message
              }
            </h3>
            <div className="otpinput-ctn">
              <MuiOtpInput
                style={{
                  width: "100%",
                  height: "100%",
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                }}
                value={otp}
                onChange={handleChange}
              />
            </div>
            <button onClick={sendRequest}>RESEND OTP</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default OTP;
