import React, { useState } from "react";
import { Box, TextField, Button, Typography } from "@mui/material";
import Backdrop from "@mui/material/Backdrop";
import { MuiOtpInput } from "mui-one-time-password-input";

import axios from "axios";
import { useNavigate } from "react-router-dom";

function Signup() {
  const [otp, setOtp] = React.useState(1);

  const [open, setOpen] = React.useState(false);
  const handleClose = () => {
    setOpen(false);
  };
  const handleToggle = () => {
    setOpen(!open);
  };

  const history = useNavigate();
  const [inputs, setInputs] = useState({
    name: "",
    email: "",
    password: "",
  });

  function handleChange(e) {
    setInputs((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  }

  const sendRequest = async () => {
    await axios
      .post("http://localhost:5000/api/signup", { name: inputs.name, email: inputs.email, password: inputs.password })
      .then((res) => {
        console.log(res.data);
        handleToggle();
        // history("/login");
      })
      .catch((err) => console.log(err));
  };

  function handleSubmit(e) {
    e.preventDefault();
    // Send http request to backend
    sendRequest();
  }

  return (
    <div>
      <Backdrop sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }} open={open} onClick={handleClose}>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
            maxWidth: "500px",
            height: "auto",
            padding: "50px",
            backgroundColor: "white",
            borderRadius: "10px",
          }}
        >
          <Typography variant="h6" component="div" sx={{ color: "black" }}>
            Email Verification Sent to !
          </Typography>
          <MuiOtpInput value={otp} onChange={handleChange} />
        </div>{" "}
      </Backdrop>
      <form style={{ margin: "0 1rem" }} onSubmit={handleSubmit}>
        <Box marginLeft="auto" marginRight="auto" maxWidth="230px" display="flex" flexDirection="column" justifyContent="center" alignItems="center">
          <Typography variant="h5">Signup</Typography>

          <TextField
            size="small"
            name="name"
            onChange={handleChange}
            value={inputs.name}
            type="text"
            label="Name"
            placeholder="Name"
            InputLabelProps={{
              shrink: true,
            }}
            margin="normal"
            fullWidth
          />
          <TextField
            size="small"
            name="email"
            onChange={handleChange}
            value={inputs.email}
            type="email"
            label="Email"
            placeholder="Email"
            InputLabelProps={{
              shrink: true,
            }}
            margin="normal"
            fullWidth
          />
          <TextField
            size="small"
            name="password"
            onChange={handleChange}
            value={inputs.password}
            type="password"
            label="Password"
            placeholder="Password"
            InputLabelProps={{
              shrink: true,
            }}
            margin="normal"
            fullWidth
          />

          <Button sx={{ marginTop: "14px" }} size="small" variant="contained" type="submit" fullWidth>
            Signup
          </Button>
        </Box>
      </form>
    </div>
  );
}

export default Signup;
