import React, { useContext } from "react";
import { AppBar, Toolbar, Typography, Box, Button } from "@mui/material";
import { Link } from "react-router-dom";
import { Context } from "./Storage";
import axios from "axios";

function Header() {
  const [state, setState] = useContext(Context);
  const { appbarStyles, typographyStyles, reactLinkStyles } = {
    appbarStyles: {
      marginBottom: "1rem",
      backgroundColor: "#fff",
      boxShadow: "0px 1px 10px -2px rgba(0,0,0,0.1)",
      color: "#000",
    },
    typographyStyles: {
      flexGrow: 1,
      fontWeight: "400",
    },
    reactLinkStyles: {
      textDecoration: "none",
      color: "#000",
    },
  };

  const logout = () => {
    axios.post("http://localhost:5000/api/logout", { token: state.token }).then((res) => {
      setState((prev) => ({ ...prev, token: "" }));
      setState((prev) => ({ ...prev, isLoggedIn: false }));
    });
  };

  return (
    <AppBar position="sticky" elevation={0} sx={appbarStyles}>
      <Toolbar>
        <Typography variant="h6" component="div" sx={typographyStyles}>
          OTP
        </Typography>
        <Box>
          {state.isLoggedIn ? (
            <Link to="/login" style={reactLinkStyles} onClick={logout}>
              <Button>Logout</Button>
            </Link>
          ) : (
            <Box>
              <Link to="/login" style={reactLinkStyles}>
                <Button>Login</Button>
              </Link>
              <Link to="/signup" style={reactLinkStyles}>
                <Button>Signup</Button>
              </Link>
            </Box>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Header;
