import React, { useState, useContext, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import { MuiOtpInput } from "mui-one-time-password-input";
import { Context } from "./Storage";
import { NotFound } from "./NotFound";

function OTP() {
  const history = useNavigate();
  const intervalRef = useRef();
  const [timer, setTimer] = useState(30);
  const [state, setState] = useContext(Context);
  const [otp, setOtp] = useState(1);
  const [otpFetched, setOtpFetched] = useState(0);
  const [data, setData] = useState({});
  const [expiredOtp, setExpiredOtp] = useState(false);
  const [token, setToken] = useState(state.token);
  const { otpInputStyle, resendButtonStyle } = {
    otpInputStyle: {
      width: "100%",
      height: "100%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    resendButtonStyle: {
      marginTop: "1rem",
    },
  };

  function getTimeRemaining(endtime){
    const total = Date.parse(endtime) - Date.parse(new Date());
    const seconds = Math.floor((total / 1000) % 60);
    return {
      total,
      seconds
    };
  }

  function startTimer(deadline){
    intervalRef.current = setInterval(() => {
      const { total, seconds } = getTimeRemaining(deadline);
      if(total <= 0){
        clearInterval(intervalRef.current);
        setExpiredOtp(true);
      } else {
        setTimer(seconds);
      }
    }, 1000);
  }

  

  const handleChange = (newValue) => {
    setOtp(newValue);
  };

  const sendRequest = async () => {
    const res = await fetch("http://localhost:5000/api/verification", {
      method: "GET",
    });

    const data = await res.json();
    console.log(data);
    setData(data);
    setOtpFetched(data.code);
    setOtp(1);
    setExpiredOtp(false);

    setTimeout(() => {
      setOtpFetched(null);
      setExpiredOtp(true);
    }, 5000);
  };

  useEffect(() => {
    if (otp === String(otpFetched)) {
      setState((prev) => ({ ...prev, isLoggedIn: true }));
      history("/user");
    }
  }, [state, otp, history, otpFetched, setState]);

  useEffect(() => {
    sendRequest();
    console.log(state.isLoggedIn);
    console.log(state);
  }, []);

  return (
    <div>
      {token === "" ? (
        <NotFound />
      ) : (
        <div className="otp-ctn">
          <div className="otp-ctn-ctn">
            <h1 className="otp-ctn-header">OTP Verification</h1>
            <h3>{data.message}</h3>
            <div className="otpinput-ctn">
              <MuiOtpInput value={otp} onChange={handleChange} style={otpInputStyle} />
            </div>
            {expiredOtp && (
              <Button variant="outlined" endIcon={<SendIcon />} onClick={sendRequest} style={resendButtonStyle}>
                RESEND OTP
              </Button>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default OTP;
