import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { Context } from "./Storage";
import CircularProgress from "@mui/material/CircularProgress";
import Box from "@mui/material/Box";

function Welcome() {
  const [mainData, setMainData] = useState({});
  const [state, setState] = useContext(Context);
  const [loaderShow, setLoaderShow] = useState(true);

  useEffect(() => {
    const sendRequest = async () => {
      await axios.get("http://localhost:5000/api/user", { headers: { Authorization: `Bearer ${state.token}` } }).then((res) => {
        setState((prev) => ({ ...prev, isLoggedIn: true }));
        setMainData(res.data);
      });
    };

    sendRequest();
    setLoaderShow(false);
  }, []);

  return (
    <>
      {!loaderShow ? (
        <div>
          <p>{state.isLoggedIn ? `Welcome ${mainData.user}` : "Please login to view this page"}</p>
          <div className="welcome-ctn">
            <h1>{`Welcome ${mainData.user} ðŸ‘‹`}</h1>
            {/* <h1>{loggedIn ? `Welcome ${data?.user} ðŸ‘‹` : `Please Login First!`}</h1> */}
          </div>
          <button
            style={{
              padding: "10px",
              backgroundColor: "#0a0a0a",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
            onClick={() => {
              console.log(mainData);
            }}
          >
            Console Log User
          </button>
        </div>
      ) : (
        <Box>
          <CircularProgress />
        </Box>
      )}
    </>
  );
}

export default Welcome;
