import React from "react";
import { MuiOtpInput } from "mui-one-time-password-input";

function OTP() {
  const [otp, setOtp] = React.useState("");
  const [otpFetched, setOtpFetched] = React.useState("");

  const handleChange = (newValue) => {
    setOtp(newValue);
  };

  React.useEffect(() => {
    // Fetch the json response from the server and check if the otpCode matches the otp state
    // If it matches, redirect the user to the user page
    // Else, show an error message

    const sendRequest = async () => {
      const res = await fetch("http://localhost:5000/api/verification", {
        method: "GET",
      });

      const data = await res.json();
      console.log(data);
    };
  }, [otp]);

  return (
    <div>
      {/* Create an OTP layout */}
      <h1>Verification</h1>
      <MuiOtpInput value={otp} onChange={handleChange} />
    </div>
  );
}

export default OTP;
