import React, { useState } from "react";
import { Box, Container, Button, TextField, Typography } from "@mui/material";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function ForgotPassword() {
  const navigate = useNavigate();

  const [inputs, setInputs] = useState({
    email: "",
  });

  function handleChange(e) {
    setInputs((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios
      .post("http://localhost:5000/api/forgot-password", { email: inputs.email })
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  };

  return (
    <Container>
      <Typography variant="h3">Find Your Account</Typography>
      <Typography variant="h6">Please enter your email to search for your account.</Typography>
      <TextField
        size="small"
        name="email"
        onChange={handleChange}
        value={inputs.email}
        type="email"
        label="Email"
        placeholder="Email"
        InputLabelProps={{
          shrink: true,
        }}
        margin="normal"
        fullWidth
      />
      <Container
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          gap: "1rem",
        }}
      >
        <Button
          sx={{
            marginTop: "14px",
            padding: "10px",
          }}
          size="medium"
          variant="outlined"
          type="submit"
          onClick={() => {
            navigate("/login");
          }}
        >
          Cancel
        </Button>
        <Button
          sx={{
            marginTop: "14px",
            padding: "10px",
          }}
          size="medium"
          variant="contained"
          type="submit"
        >
          Search
        </Button>
      </Container>
    </Container>
  );
}

export default ForgotPassword;
