      set
    }, 10000);
  }, []);

  return (
    <div>
      {token === "" ? (
        <NotFound />
      ) : (
        <div className="otp-ctn">
          <h1>Verification</h1>
          <MuiOtpInput value={otp} onChange={handleChange} />
        </div>
      )}
    </div>
  );
}

export default OTP;
