import React, { useContext } from "react";
import { AppBar, Toolbar, Typography, Box, Tab, Tabs } from "@mui/material";
import { Link } from "react-router-dom";
import { Context } from "./Storage";

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

function Header() {
  const [state, setState] = useContext(Context);
  const [value, setValue] = React.useState(0);
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div>
      <AppBar
        position="sticky"
        sx={{ marginBottom: "1rem" }}
        elevation={0}
        // Change background color

        style={{
          backgroundColor: "#fff",
          boxShadow: "0px 1px 10px -2px rgba(0,0,0,0.1)",
          color: "#000",
        }}
      >
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, 
            // 
          }}>
            OTP Verification
          </Typography>
          <Box>
            {state.isLoggedIn ? (
              <Tabs value={value} onChange={handleChange} aria-label="secondary tabs example">
                <Tab label="Logout" component={Link} to="/login" />
              </Tabs>
            ) : (
              <Tabs value={value} onChange={handleChange} aria-label="secondary tabs example">
                <Tab label="Login" component={Link} to="/login" />
                <Tab label="Signup" component={Link} to="/signup" />
              </Tabs>
            )}
          </Box>
        </Toolbar>
      </AppBar>
    </div>
  );
}

export default Header;
