import React, { useState, useEffect } from "react";
import { Container, Button, Typography, TextField } from "@mui/material";
import axios from "axios";

function ChangePassword() {
  const [inputs, setInputs] = useState({
    newPassword: "",
    confirmPassword: "",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios
      .post("http://localhost:5000/api/forgot-password/:id", { newPassword: inputs.email, confirmPassword: inputs.email })
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => console.log(err));
  };

  const handleChange = (e) => {
    setInputs((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

    useEffect(() => {},[])


  return (
    <div>
      <form
        style={{
          textAlign: "center",
        }}
        onSubmit={handleSubmit}
      >
        <Typography
          style={{
            marginTop: "10rem",
          }}
          variant="h5"
        >
          Enter a New Password
        </Typography>
        <Container
          sx={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
            textAlign: "center",
            gap: "5px",
            marginTop: "1rem",
          }}
        >
          <TextField
            size="small"
            name="newPassword"
            onChange={handleChange}
            value={inputs.newPassword}
            type="password"
            label="New Password"
            placeholder="Enter a New Password"
            InputLabelProps={{
              shrink: true,
            }}
            margin="normal"
          />
          <TextField
            size="small"
            name="confirmPassword"
            onChange={handleChange}
            value={inputs.confirmPassword}
            type="password"
            label="Confirm Password"
            placeholder="Confirm Password"
            InputLabelProps={{
              shrink: true,
            }}
            margin="normal"
          />

          <Button
            type="submit"
            sx={{
              marginTop: "14px",
            }}
            variant="outlined"
          >
            Change Password
          </Button>
        </Container>
      </form>
    </div>
  );
}

export default ChangePassword;
