import React, { useContext } from "react";
import { AppBar, Toolbar, Typography, Box, Tab, Tabs } from "@mui/material";
import { Link } from "react-router-dom";
import { Context } from "./Storage";

function Header() {
  const [state, setState] = useContext(Context);

  return (
    <div>
      <AppBar
        position="sticky"
        sx={{ marginBottom: "1rem" }}
        elevation={0}
        // Change background color

        style={{
          backgroundColor: "#fff",
          boxShadow: "0px 1px 10px -2px rgba(0,0,0,0.1)",
          color: "#000",
        }}
      >
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            OTP
          </Typography>
          <Box>
            <Tabs textColor="inherit" TabIndicatorProps={{ style: { background: "red" } }}>
              {state.isLoggedIn ? (
                <Box>
                  <Tab label="Logout" component={Link} to="/login" value={0} />
                </Box>
              ) : (
                <Box>
                  <Tab label="Login" component={Link} to="/login" value={0} />
                  <Tab label="Signup" component={Link} to="/signup" value={1} />
                </Box>
              )}
            </Tabs>
          </Box>
        </Toolbar>
      </AppBar>
    </div>
  );
}

export default Header;
