import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Box, TextField, Button, Typography } from "@mui/material";
import Backdrop from "@mui/material/Backdrop";
import ClearIcon from "@mui/icons-material/Clear";

function Signup() {
  const history = useNavigate();
  const [data, setData] = useState({});
  const [open, setOpen] = useState(false);
  const [otp, setOtp] = useState(1);
  const [otpFetched, setOtpFetched] = useState(0);

  const [inputs, setInputs] = useState({
    name: "",
    email: "",
    password: "",
  });

  function handleChange(e) {
    setInputs((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  }

  function otpHandleChange(e) {
    setOtp(e);
  }

  const handleClose = () => {
    setOpen(false);
  };

  const handleToggle = () => {
    setOpen(!open);
  };

  const sendRequest = async () => {
    await axios
      .post("http://localhost:5000/api/signup", { name: inputs.name, email: inputs.email, password: inputs.password })
      .then((res) => {
        console.log(res.data);
        setOtpFetched(res.data.code);
        setData(res.data);
        handleToggle();

        // history("/login");
      })
      .catch((err) => console.log(err));
  };

  function handleSubmit(e) {
    e.preventDefault();
    sendRequest();
  }

  useEffect(() => {
    console.log(otp, otpFetched);
    if (otp === String(otpFetched)) {
      console.log("Verified Account");
    }
  }, [otp, otpFetched]);

  function newEmailVerification() {
    axios.patch("http://localhost:5000/api/update-verified/send-email", { email: inputs.email }).then((res) => {
      setOpen(true);
      console.log(res);
      history("/login");
    });
  }

  return (
    <div>
      <Backdrop sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }} open={open} onClick={handleClose}>
        <Box
          sx={{
            padding: "30px",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "white",
            overflowX: "hidden",
            borderRadius: "10px",
            margin: "0 10px",
          }}
        >
          <Typography variant="h6" component="div" sx={{ textAlign: "center", color: "black", maxWidth: "500px", lineHeight: "1" }}>
            {data.message}
          </Typography>
        </Box>
      </Backdrop>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          margin: "0 2rem",
        }}
        onSubmit={handleSubmit}
      >
        <Box maxWidth="200px">
          <Typography variant="h5">Signup</Typography>

          <TextField
            size="small"
            name="name"
            onChange={handleChange}
            value={inputs.name}
            type="text"
            label="Name"
            placeholder="Name"
            InputLabelProps={{
              shrink: true,
            }}
            margin="normal"
            fullWidth
          />
          <TextField
            size="small"
            name="email"
            onChange={handleChange}
            value={inputs.email}
            type="email"
            label="Email"
            placeholder="Email"
            InputLabelProps={{
              shrink: true,
            }}
            margin="normal"
            fullWidth
          />
          <TextField
            size="small"
            name="password"
            onChange={handleChange}
            value={inputs.password}
            type="password"
            label="Password"
            placeholder="Password"
            InputLabelProps={{
              shrink: true,
            }}
            margin="normal"
            fullWidth
          />

          <Button sx={{ marginTop: "14px" }} size="small" variant="contained" type="submit" fullWidth>
            Signup
          </Button>
        </Box>
      </form>
    </div>
  );
}

export default Signup;
